exec("import re\nencoding=\"utf-8\"  # Change this if you're using it with a different encoding\nfilename = input(\"File: \")\nwith open(f\"./{filename}\", \"r\", encoding=encoding) as fp:\n\tfile = fp.read()\n\tnewfile = \"\"\n\treplace = False\n\tlastchar = \"\"\n\tfor char in file:\n\t\tif char == \"\\t\":\n\t\t\tnewfile += \"\\\\t\"\n\t\t\treplace = True\n\n\t\tif char == \"\\n\":\n\t\t\tnewfile += \"\\\\n\"\n\t\t\treplace = True\n\n\t\tif char == \"\\\"\":\n\t\t\tnewfile += \"\\\\\\\"\"\n\t\t\treplace = True\n\n\t\tif char == \"\\\\\":\n\t\t\tnewfile += \"\\\\\\\\\"\n\t\t\treplace\t= True\n\n\t\tif not replace:\n\t\t\tnewfile += char\n\n\t\treplace = False\n\t\tlastchar = char\n\n\twith open(f\"./{filename}-exec.py\", \"w\", encoding=encoding) as fp2:\n\t\tfp2.write(f\"exec(\\\"{newfile}\\\", globals())\")\n\n\tprint(\"Turned file into exec\")", globals())